div> <label>Category</label> <div class="form-group"> <select class="form-control" name="category"> <option value="0">Uncategorized</option> <?php $__currentLoopData = $categories; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $key => $value): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <option value="<?php echo e($value->id); ?>"><?php echo e($value->name); ?></option> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> </select> </div> <div class="form-group"> <label>Featured image</label> <div class="form-group"> <img id="previewImage_-" data-toggle="modal" data-target="#modal-galleries" src="<?php echo e(asset('uploaded/media/default.jpg')); ?>" style="width:100%; height:195px;"> <input type="hidden" name="image" value="default.jpg" id="targetValue_-"> <?php echo $__env->make('admin.images.modals', array_except(get_defined_vars(), array('__data', '__path')))->render(); ?> </div> </div> </div> </div> <hr> <div class="row"> <div class="col-md-12 text-right"> <button type="submit" class="btn btn-primary">Add new page</button> </div> </div> </div> </form> </div> </div> <?php $__env->stopSection(); ?>
