 <?php $__env->startSection('title', 'Pages'); ?> <?php $__env->startSection('contents'); ?> <a href="<?php echo e(route('pages_create')); ?>" class="new-btn" title="New Pages"><i class="glyphicon glyphicon-pencil"></i></a> <div class="bg-light lter b-b wrapper-md"> <h1 class="m-n font-thin h3">Pages</h1> </div> <div class="wrapper-md"> <?php if( Session::has('success') ): ?> <div class="alert alert-success" role="alert"> <button type="button" class="close" data-dismiss="alert"> <span aria-hidden="true">&times;</span> <span class="sr-only">Close</span> </button> <?php echo e(session('success')); ?> </div> <?php endif; ?> <form action="<?php echo e(route('pages')); ?>" method="get"> <div class="form-group"> <input type="type" class="form-control input-lg" name="key" placeholder="Pencarian"> </div> </form> <div class="panel"> <div class="table-responsive"> <table class="table table-striped m-b-none"> <thead> <tr> <th style="width: auto">Page Title</th> <th style="width: auto">Category</th> <th style="width: auto">Create</th> <th style="width:120px;"></th> </tr> </thead> <tbody> <?php $__empty_1 = true; $__currentLoopData = $pages; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $p): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); $__empty_1 = false; ?> <tr> <td> <a href="<?php echo e(route('pages_detail', [ 'slug' => $p->slug ])); ?>"><?php echo e($p->title); ?></a> </td> <td><?php echo e(isset($p->Category[0]->name)==true?$p->Category[0]->name:'Uncategorized'); ?></td> <td><?php echo e(Carbon\Carbon::parse($p->create_at)->format('d F Y')); ?></td> <td> <a href="<?php echo e(route('pages_edit', [ 'id' => $p->id ])); ?>" class="btn btn-default btn-xs">Edit</a> <button class="btn btn-danger btn-xs" data-toggle="modal" data-target="#modal-delete" data-id="<?php echo e($p->id); ?>">Delete</button> </td> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); if ($__empty_1): ?> <tr> <td colspan="6" align="center">No pages, please create a new pages or <a href='<?php echo e(route('pages')); ?>'>Back</a></td> </tr> <?php endif; ?> </tbody> </table> </div> </div> <div class="text-center m-t-lg m-b-lg"> <ul class="pagination pagination-md"> <?php echo e($pages->appends(request()->except('page'))->links()); ?> </ul> </div> </div> <?php $__env->stopSection(); ?> <?php $__env->startSection('modal'); ?> <div class="modal fade" id="modal-delete" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"> <div class="modal-dialog modal-sm" role="document"> <form action="<?php echo e(route('pages_delete')); ?>" method="post"> <?php echo e(csrf_field()); ?> <div class="modal-content"> <div class="modal-header"> <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button> <h4 class="modal-title" id="myModalLabel">Delete Page</h4> </div> <div class="modal-body"> <input type="hidden" name="id"> Are you sure you want to delete this page? </div> <div class="modal-footer"> <button type="button" class="btn btn-default" data-dismiss="modal">No</button> <button type="submit" class="btn btn-danger">Yes</button> </div> </div> </form> </div> </div> <?php $__env->stopSection(); ?>
